import React from 'react'
import {   Button,  Col,  Row } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import { useParams } from 'react-router-dom'

function MoveDetails() {
  const id = useParams()
  console.log(id.id);
  // get  movies
  const getMovieyById = async () => {
    try {
      const res = await axios.get(
        "https://api.themoviedb.org/3/movie/popular?language=ar",
        {
          headers: {
            Authorization:
              "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiJmZjI1Njg1MTRjMTUzOTdkOTFhMjMwMjVmMTAxODE1MSIsInN1YiI6IjY0YTViMjE0YTBiZTI4MDEyZWVmZDRkNyIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.PvG6gxDTuOf5pJKAfy7BddWaJBAqG581Fp0UhYGBQGw",
            accept: "application/json",
          },
        }
      );
      setMovies(res.data.results);
   
      setPageCount(res.data.total_pages)
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <div  > 
    <Row >
      <div className='total-content  '>
 <Col md={6} className='' >
       <div className='image content'>
         <img
          src='https://th.bing.com/th/id/OIP.1YM53mG10H_U25iPjop83QHaEo?pid=ImgDet&rs=1' 
          alt='img' />
       </div>
  </Col>
          <Col md={6} className='content'>
              <div className='details'>
                <p>  اسم الفيلم: فيلم</p>
                <span className='line'></span>
                <p>  تاريخ الفيلم: تاريخ</p>
                <span className='line'></span>
                <p>  عدد المقيمين: عدد</p>
                <span className='line'></span>
                <p>   التقييم: 8.5</p>
                <span className='line'></span>
              </div>
          </Col>
      </div>
    </Row>
    <Row>
        <Col md={12} sm={12} className='content'>
          <div className='text'>
          <h3 className='text-title'>: القصة </h3>
          <span className='line-title'></span>
          <p>قصة الفيلم  </p>
          </div>
          </Col>
    
    </Row>
   <Row>
   <Col md={12 } sm={12}>
       <div className='buttons content'>http://localhost:3000/movie/455476
         <Link to={'/'}>
          <Button to={'/'} variant='success mx-2'> عودة للرئيسية </Button>
         </Link>
         <Button> مشاهدة الفيلم </Button>
       </div>
      </Col>
   </Row>
   </div>
  )
}

export default MoveDetails
