import React, { useEffect, useState } from 'react';
import { Button, Col, Row } from 'react-bootstrap';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';

function MoveDetails() {
  const params = useParams();
  const [movie, setMovie] = useState({});
  


  // get movie by id
  const getMovieById = async () => {
    try {
      const res = await axios.get(
        `https://api.themoviedb.org/3/movie/${params.id}?language=ar`,
        {
          headers: {
            Authorization:
              'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiJmZjI1Njg1MTRjMTUzOTdkOTFhMjMwMjVmMTAxODE1MSIsInN1YiI6IjY0YTViMjE0YTBiZTI4MDEyZWVmZDRkNyIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.PvG6gxDTuOf5pJKAfy7BddWaJBAqG581Fp0UhYGBQGw',
            accept: 'application/json',
          },
        }
      );
      setMovie(res.data);
    
    } catch (error) {
      console.log(error);
    }
  };


  useEffect(() => {
    getMovieById();
    
  }, [params.id]); // Include params.id as a dependency to re-fetch the movie when the ID changes
  

  return (
    <>
       <Row>
        <Col md={6}>
          <Image src={singleProduct.image} alt={singleProduct.name} fluid />
        </Col>
        <Col md={3}>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h3>{singleProduct.name}</h3>
            </ListGroup.Item>

            <ListGroup.Item>
              <Rating
                value={singleProduct.rating}
                text={`${singleProduct.numReview} Reviews`}
              />
            </ListGroup.Item>

            <ListGroup.Item>Price: {singleProduct.price} $</ListGroup.Item>
            <ListGroup.Item>
              Description: {singleProduct.description}
            </ListGroup.Item>
            <ListGroup.Item>
              Weight: {singleProduct.weight} kg
            </ListGroup.Item>
          </ListGroup>
        </Col>

        <Col md={3}>
          <Card>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <Row>
                  <Col>Price:</Col>
                  <Col>
                    <strong>{singleProduct?.price} $</strong>
                  </Col>
                </Row>
              </ListGroup.Item>

              <ListGroup.Item>
                <Row>
                  <Col>Status:</Col>
                  <Col>
                    {singleProduct?.countInStock > 0
                      ? "inStock"
                      : "out of stock"}
                  </Col>
                </Row>
              </ListGroup.Item>

              <ListGroup.Item>
                <Row>
                    <h1> </h1>
                    <p> Count products: {productCount}</p>
                    </Row>
              </ListGroup.Item>

               <ListGroup.Item>
                <Row>
                <Col>
                    <Col>Count:</Col>
                    <Col >
                    <Button style={{borderRadius: "20%", margin: "0 10px"} }
                      onClick={increaseCount}
                      variant="info"
                      type="button"
                      disabled={singleProduct.countInStock === 0}
                    >
                      +
                    </Button>
                    
                    <Button style={{borderRadius: "20%", margin: "0 10px"} }
                      onClick={decreaseCount}
                      variant="warning"
                      type="button"
                      disabled={singleProduct.countInStock === 0}
                    >
                      -
                    </Button>
                    </Col>
                    
                  </Col>
                </Row>
               </ListGroup.Item>

                <ListGroup.Item>
                    <Row>
                    <Col>Total Price:</Col>
                     <Col>{totalPrice()} $</Col>
                    </Row>
                </ListGroup.Item>
                
                <ListGroup.Item>
                    <Row>
                    <Col>TotalWeight:</Col>
                     <Col>{itemsWeight()} kg</Col>
                    </Row>
                </ListGroup.Item>
             
                
           
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </>
    // <div>
    //   <Row>
    //     <div className="total-content">
    //       <Col md={6}>
    //         <div className="image content">
    //           <img
    //             src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
    //             alt="img"
    //           />
    //         </div>
    //       </Col>
    //       <Col md={6} sm={12} className="content ">
    //         <div className="details">
    //           <p style={{ margin: '10px' }}>اسم الفيلم: {movie.title}</p>
    //           <p style={{ margin: '10px' }}>تاريخ الفيلم: {movie.release_date}</p>
    //           <p style={{ margin: '10px' }}>عدد المقيمين: {movie.vote_count}</p>
    //           <p style={{ margin: '10px' }}>التقييم: {movie.vote_average}</p>
    //         </div>
    //       </Col>
    //     </div>
    //   </Row>
    //   <Row>
    //     <Col md={12} sm={12} className="content">
    //       <div className="text">
    //         <h3 className="text-title">القصة:</h3>
    //         <span className="line-title"></span>
    //         <p>{movie.overview}</p>
    //       </div>
    //     </Col>
    //   </Row>
    //   <Row>
    //     <Col md={12} sm={12}>
    //       <div className="buttons content">
    //         <Link to="/">
    //           <Button variant="success mx-2">عودة للرئيسية</Button>
    //         </Link>
    //         <a href={movie.homepage} target="_blank">
    //           <Button>مشاهدة الفيلم</Button>
    //         </a>
         
    //       </div>
    //     </Col>
    //   </Row>
    // </div>
  );
}

export default MoveDetails;
